r') ?>');        $('a.brand-link').removeClass('navbar-dark');        // Sidebar        // $('aside.main-sidebar').removeClass('sidebar-dark-<?= env('theme.color') ?>');        // $('aside.main-sidebar').addClass('sidebar-light-<?= env('theme.color') ?>');        localStorage.setItem("mode", "light");    }    function status(title, text) {        Swal.fire({            icon: 'info',            title: title,            text: text,        })    }    $(function() {        if (localStorage.getItem("mode") == "dark") {            darkmode();        } else {            lightmode();        }        $("#switch-dark").on("change", function() {            if ($(this).prop("checked")) {                darkmode();            } else {                lightmode();            }        });                $(document).ajaxStart(function() {             Pace.restart();         });        <?php if (!is_admin()): ?>        let countNotification = $("#notification-body").find('a.dropdown-item').length;        if (countNotification > 0) {            $("#notification-count").text(countNotification);        }        <?php endif; ?>        $('.custom-file-input').on('change', function() {            let fileName = $(this).val().split('\\').pop();            $(this).next('.custom-file-label').addClass("selected").html(fileName);        });        $('body').tooltip({            selector: '[data-toggle="tooltip"]'        });        $('.select2').select2();        $.fn.dataTableExt.oApi.fnPagingInfo = function(oSettings) {            return {                "iStart": oSettings._iDisplayStart,                "iEnd": oSettings.fnDisplayEnd(),                "iLength": oSettings._iDisplayLength,                "iTotal": oSettings.fnRecordsTotal(),                "iFilteredTotal": oSettings.fnRecordsDisplay(),                "iPage": Math.ceil(oSettings._iDisplayStart / oSettings._iDisplayLength),                "iTotalPages": Math.ceil(oSettings.fnRecordsDisplay() / oSettings._iDisplayLength)            };        };        $.fn.DataTable.ext.pager.numbers_length = 5;                $.extend(true, $.fn.dataTable.defaults, {            "language": {                "emptyTable": "Belum ada data",                "lengthMenu": "_MENU_ baris perhalaman",                "zeroRecords": "Data kosong",                "info": "Halaman _PAGE_ dari _PAGES_",                "infoEmpty": "Data kosong",                "infoFiltered": "(Disaring dari _MAX_ total baris)",                "search": "Cari:",                "loadingRecords": "Loading...",                "processing": "Sedang Proses...",                "paginate": {                    "first": "«",                    "last": "»",                    "next": "›",                    "previous": "‹"                },            }        });        // SwalToast        const Toast = Swal.mixin({            toast: true,            position: 'top',            showConfirmButton: false,            timer: 3000,            timerProgressBar: true,            didOpen: (toast) => {                toast.addEventListener('mouseenter', Swal.stopTimer)                toast.addEventListener('mouseleave', Swal.resumeTimer)            }        });        // Memanggil fungsi toast pada helper         <?= session()->getFlashdata('toast'); ?>        <?= session()->getFlashdata('alert'); ?>        // Tooltip        $('[data-toggle="tooltip"]').tooltip()        // Datatable        var table = $(".datatable").DataTable({            buttons: ['copy', 'csv', 'print', 'excel'],            dom: "<'row px-2 px-md-4 pt-2'<'col-md-6 text-left'B><'col-md'f>>" +                "<'row'<'col-md-12'tr>>" +                "<'row px-2 px-md-4 py-3'<'col-md-5'i><'col-md-7'p>>",            lengthMenu: [                [20, 50, 100, -1],                [20, 50, 100, "All"]            ],            responsive: true,            columnDefs: [{                targets: -1,                orderable: false,                searchable: false            }]        });        table.buttons().container().appendTo('#dataTable_wrapper .col-md-5:eq(0)');        // Datepicker        let datepickerConfig = {            uiLibrary: 'bootstrap4',            format: 'yyyy-mm-dd',            showRightIcon: true        }        $.each($(".gijgo"), function(i, input) {            $("#" + input.id).datepicker(datepickerConfig);        });    });    </script>    <?= $this->renderSection('addons'); ?></body></html>