omor-vlegal').val(result.noVlegal);            $('#modalTTD #btn-getnomor').removeClass('disabled');            $('#modalTTD #btn-getnomor').html(`<i class="fa fa-sync-alt"></i> Check`);        });    }    $(function(){        $('#modalTTD #btn-getnomor').on('click', function() {            getNomor();        });    });    $('#modalTTD').on('shown.bs.modal', function () {        getNomor();    });</script><?= $this->endSection(); ?>